  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Create a Document'
};
export const metadata = {
  "unversionedId": "create-a-doc",
  "id": "create-a-doc",
  "isDocsHomePage": false,
  "title": "Create a Document",
  "description": "This page will help you to create your first document with Docusaurus.",
  "source": "@site/docs/create-a-doc.md",
  "slug": "/create-a-doc",
  "permalink": "/docs/create-a-doc",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs/create-a-doc.md",
  "version": "current",
  "sidebar": "docs",
  "previous": {
    "title": "Markdown Features",
    "permalink": "/docs/markdown-features"
  },
  "next": {
    "title": "Create a Page",
    "permalink": "/docs/create-a-page"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Creating a simple document',
  id: 'creating-a-simple-document',
  children: []
}, {
  value: 'Adding your document to the sidebar',
  id: 'adding-your-document-to-the-sidebar',
  children: []
}, {
  value: 'Making your document as the home page',
  id: 'making-your-document-as-the-home-page',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`This page will help you to create your first document with Docusaurus.`}</p>
    <h2 {...{
      "id": "creating-a-simple-document"
    }}>{`Creating a simple document`}</h2>
    <p>{`Create a markdown file `}<inlineCode parentName="p">{`docs/hello.md`}</inlineCode>{`.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-md"
      }}>{`website # root directory of your site
├── docs
│   └── hello.md
├── src
│   └── pages
├── docusaurus.config.js
├── ...
`}</code></pre>
    <p>{`At the top of the file, specify the `}<inlineCode parentName="p">{`id`}</inlineCode>{` and `}<inlineCode parentName="p">{`title`}</inlineCode>{` of your document. The purpose is so that Docusaurus can identify your document when generating your site. After that you can start adding your document's content.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-mdx"
      }}>{`---
id: hello 
title: Hello, World! 
---

## Hello, World!

This is your first document in **Docusaurus**, Congratulations!
`}</code></pre>
    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`note`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`By default, id of the document is the `}<inlineCode parentName="p">{`filename`}</inlineCode>{` of the document. So, you can omit it if your filename already resembles the id.`}</p></div></div>
    <p>{`After creating and saving the file, run your development server and go to `}<inlineCode parentName="p">{`http://localhost:3000/docs/hello`}</inlineCode>{` in your browser.`}</p>
    <h2 {...{
      "id": "adding-your-document-to-the-sidebar"
    }}>{`Adding your document to the sidebar`}</h2>
    <p>{`You can add `}<inlineCode parentName="p">{`hello.md`}</inlineCode>{` to the sidebar by adding it to `}<inlineCode parentName="p">{`sidebars.js`}</inlineCode></p>
    <p>{`Open `}<inlineCode parentName="p">{`sidebars.js`}</inlineCode>{` and add the `}<inlineCode parentName="p">{`id`}</inlineCode>{` of `}<inlineCode parentName="p">{`hello.md`}</inlineCode>{` which is `}<inlineCode parentName="p">{`hello`}</inlineCode>{` to the items in docs sidebar.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js"
      }}>{`module.exports = {
  docs: [
    {
      type: 'category',
      label: 'Docusaurus Tutorial',
      items: ['getting-started', 'markdown-features', 'create-a-doc', 'hello'],
    },
  ],
};
`}</code></pre>
    <p>{`Check your sidebar now, and you can access `}<inlineCode parentName="p">{`hello.md`}</inlineCode>{` from the docs sidebar now!`}</p>
    <h2 {...{
      "id": "making-your-document-as-the-home-page"
    }}>{`Making your document as the home page`}</h2>
    <p>{`If you want to make `}<inlineCode parentName="p">{`hello.md`}</inlineCode>{` as your docs home page, then you can add `}<inlineCode parentName="p">{`slug`}</inlineCode>{` to the header fields.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-mdx"
      }}>{`---
id: hello
title: Hello, World! 
slug: /
---

Hello, World!
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
