  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'overview',
	title: 'B4LL Architecture',
	sidebar_label: 'Overview'
};
export const metadata = {
  "unversionedId": "architecture/overview",
  "id": "architecture/overview",
  "isDocsHomePage": false,
  "title": "B4LL Architecture",
  "description": "This section describes the architecture proposed for the B4LL project. The",
  "source": "@site/docs/architecture/overview.md",
  "slug": "/architecture/overview",
  "permalink": "/b4ll-docs/docs/architecture/overview",
  "version": "current",
