  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'app',
	title: 'Communication Channels',
	sidebar_label: 'Communication Channels',
	slug: '/app'
};
export const metadata = {
  "unversionedId": "architecture/app",
  "id": "architecture/app",
  "isDocsHomePage": false,
  "title": "Communication Channels",
  "description": "Communication channels act as a point of interaction between the user and the",
  "source": "@site/docs/architecture/app.md",
  "slug": "/app",
  "permalink": "/b4ll-docs/docs/app",
  "version": "current",
  "sidebar_label": "Communication Channels",
  "sidebar": "someSidebar",
  "previous": {
    "title": "B4LL Architecture",
    "permalink": "/b4ll-docs/docs/architecture/overview"
  },
  "next": {
    "title": "Engine",
    "permalink": "/b4ll-docs/docs/architecture/engine"
