  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Introduction',
	sidebar_label: 'Introduction'
};
export const metadata = {
  "unversionedId": "voice/intro",
  "id": "voice/intro",
  "isDocsHomePage": false,
  "title": "Introduction",
  "description": "Voice is one of the biometric types approachd in the B4LL project to demonstrate",
  "source": "@site/docs/voice/intro.md",
  "slug": "/voice/intro",
  "permalink": "/b4ll-docs/docs/voice/intro",
